// Minimal ScrollReveal shim to avoid external CDN and tracking-prevention warnings.
// Provides the same API surface used in this project: `ScrollReveal({...}).reveal(selector, cfg)`
(function(global){
  function ScrollReveal(options){
    return {
      reveal: function(selector, cfg){
        // no-op: leave animations to CSS or to the original library when online.
        // Keep this asynchronous-friendly in case code expects a Promise-like behavior.
        return null;
      }
    };
  }
  global.ScrollReveal = ScrollReveal;
})(typeof window !== 'undefined' ? window : this);
